import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom'
import PropTypes from 'prop-types'

const AddExperience = props => {
  const { addExperience, history } = props
  const [formData, setFormData] = useState({
    title: '',
    location: '',
    from: '',
    to: '',
    current: false,
    description: ''
  })

  const [toDateDisabled, toggleDisabled] = useState(false)

  const { 
    title,
    location,
    from,
    to, 
    current, 
    description} = formData

  const onChange = (e )=> {
    // console.log(formData)
    setFormData({
      ...formData,
     [e.target.name]: e.target.value

    })
  }

  return (
    <div className="container mx-4 my-6">
      <h1 className="large text-primary">
       Add An Experience
      </h1>
      <p className="lead">
        <i className="fas fa-code-branch"></i> Tell us about any races that you have been participated in
      </p>
      <small>* = required field</small>
      <form className="form"
      onSubmit={e => {
        e.preventDefault()
        addExperience(formData, history, true)
      }}>
        <div className="form-group">
          <input type="text" placeholder="* Job Title" name="title" 
          value={title}
          onChange={e => onChange(e)}
          required />
        </div>
        <div className="form-group">
          <input type="text" placeholder="Location" name="location"
                    value={location}
                    onChange={e => onChange(e)} />
        </div>
        <div className="form-group">
          <h4>From Date</h4>
          <input type="date" name="from" 
                    value={from}
                    onChange={e => onChange(e)}/>
        </div>
         <div className="form-group">
          <p><input type="checkbox" name="current"
                    checked={current}
                    value={current}
                    onChange={e => {
                      setFormData({...formData, current: !current })
                      toggleDisabled(!toDateDisabled)
                    }}/> {' '}Current Job</p>
        </div>
        <div className="form-group">
          <h4>To Date</h4>
          <input type="date" name="to" 
          disabled={toDateDisabled ? 'disabled': ''}
                    value={to}
                    onChange={e => onChange(e)}/>
        </div>
        <div className="form-group">
          <textarea
            name="description"
            cols="30"
            rows="5"
            placeholder="Job Description"
            value={description}
            onChange={e => onChange(e)}
          ></textarea>
        </div>
        <input type="submit" className="btn btn-primary my-1" />
        <Link className="btn btn-light my-1" to="/dashboard">Go Back</Link>
      </form>

    </div>
  )
}

AddExperience.propTypes = {
  addExperience: PropTypes.func.isRequired
}

export default AddExperience
